mapper.select:
select r.* , a.airplane_id from route_fa r, airplane_fa a
where r.airplane_id = a.airplane_id 
and to_char(r.departure_date, 'YYYY-MM-DD')  < = #{departure_date_end}
and to_char(r.departure_date, 'YYYY-MM-DD') >= #{departure_date_start}

and r.departure_name = #{departure_name}


and r.arrival_name = #{arrival_name}


and r.route_num = #{route_num}

order by route_num

		select count(*) from route_fa
 		where to_char(departure_date, 'YYYY-MM-DD')  < = #{departure_date_end}
		and to_char(departure_date, 'YYYY-MM-DD') >= #{departure_date_start}

		and departure_name = #{departure_name}


		and arrival_name = #{arrival_name}


		and route_num = #{route_num}

select * from route_fa r, airplane_fa a 
	where r.route_num = #{r.route_num}
	and r.airplane_id = a.airplane_id

select * from reservation_fa r, schedule_fa s
where r.reservation_num = s.reservation_num
and s.route_num = #{route_num}

select a.*, case when b.count = '1' then '편도'when b.count = '2' then '왕복' else '다구간' end as reservation_type from (select s.reservation_num, r.customer_id, s.passenger_name, rou.departure_name, rou.arrival_name, rou.departure_date, r.reservation_date, r.payment, r.reservation_state from reservation_fa r, schedule_fa s, route_fa rou where r.reservation_num = s.reservation_num and s.route_num = rou.route_num and s.customer_id = r.customer_id and s.type = '출발'
and to_char(rou.departure_date, 'YYYY-MM-DD') >= #{departure_date_start}
and to_char(rou.departure_date, 'YYYY-MM-DD') < = #{departure_date_end}
and to_char(r.reservation_date, 'YYYY-MM-DD') >= #{reservation_date_start}
and to_char(r.reservation_date, 'YYYY-MM-DD') < = #{reservation_date_end}
and rou.departure_name = #{departure_name}
and rou.arrival_name = #{arrival_name}
and c.customer_name like '%'||#{customer_name}||'%'
and s.type = '출발' group by s.reservation_num, r.customer_id, s.passenger_name, rou.departure_name, rou.arrival_name, rou.departure_date, r.reservation_date, r.payment, r.reservation_state) a, (select reservation_num, count(*) count from ( select s.reservation_num, s.type from reservation_fa r, schedule_fa s where r.reservation_num = s.reservation_num group by s.reservation_num, s.type) a group by reservation_num) b where a.reservation_num = b.reservation_num order by b.reservation_num desc

		select count(*)
		    from reservation_fa r, schedule_fa s, route_fa rou
	        where r.reservation_num = s.reservation_num 
	        and s.route_num = rou.route_num
	        and s.customer_id = r.customer_id
	        and s.type = '출발'

            and to_char(rou.departure_date, 'YYYY-MM-DD') >= #{departure_date_start}


			   and to_char(rou.departure_date, 'YYYY-MM-DD')  < = #{departure_date_end}


			  and to_char(r.reservation_date, 'YYYY-MM-DD') >= #{reservation_date_start}


			  and to_char(r.reservation_date, 'YYYY-MM-DD')  < = #{reservation_date_end}


			and rou.departure_name = #{departure_name}


			and rou.arrival_name = #{arrival_name}


			and c.customer_name like  '%'||#{customer_name}||'%'

		    and s.type = '출발'

mapper.update:
update reservation_fa
set reservation_state = '예약취소'
where reservation_num = #{reservation_num}
